import React, { useEffect, useState, useRef } from 'react';

import './Navbar.css';
import { Link } from 'react-router-dom';
import {GiHamburgerMenu} from 'react-icons/gi'
import {MdOutlineRestaurantMenu} from 'react-icons/md'
import Checkbox from '../Checkbox/Checkbox';

const Navbar = () => {

  const [isMenuOpen, setIsMenuOpen] = useState(false)

  const menuRef = useRef(null);
  const checkboxRef = useRef(null);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const clickOutsideMenu = (event) => {
    if (menuRef.current && !menuRef.current.contains(event.target)) {
      setIsMenuOpen(false)
    }
  };

  useEffect(() => {
    document.addEventListener('click', clickOutsideMenu, true);
    return () => {
        document.removeEventListener('click', clickOutsideMenu, true);
    };
}, []);

  return (
    <div className='cv__navbar'>
      <div className='cv__navbar-heading'>
        <div className='cv__navbar-heading_square'></div>
        <Link to='/'>
          <div className='cv__navbar-heading_links'>
            <h1 className='inline'>Alok Awasthi</h1>
            <p className='inline'> / SOFTWARE DEVELOPER </p>
          </div>
        </Link>
       </div>

      <ul className='cv__navbar-links'>
        <li>ABOUT ME</li>
        <li>RESUME</li>
        <li>EXPERIENCE</li>
        <li>CONTACT</li>
      </ul>

      <div className='cv__navbar-smallscreen'>
        <div className='cv__navbar-smallscreen_menu'>
          <Checkbox checked={isMenuOpen} onChange={toggleMenu}></Checkbox>
        </div>
        {isMenuOpen && (
          <div ref={menuRef} className={isMenuOpen ? 'cv__navbar-smallscreen_overlay active' : 'cv__navbar-smallscreen_overlay'}>
            <ul className='cv__navbar-smallscreen_links'>
              <li>ABOUT ME</li>
              <li>RESUME</li>
              <li>EXPERIENCE</li>
              <li>CONTACT</li>
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}
export default Navbar;
