import React, { useState } from 'react';

import './Navbar.css';
import { Link } from 'react-router-dom';
import {GiHamburgerMenu} from 'react-icons/gi'
import {MdOutlineRestaurantMenu} from 'react-icons/md'

const Navbar = () => {

  const [toggleMenu, setToggleMenu] = useState(false)
  const [fix, setFix] = useState(false)

  function setFixed() {
    if(window.scrollY > 0) {
      setFix(true)
    }
  }

  return (
    <div className='cv__navbar'>
      <div className='cv__navbar-heading'>
        <div className='cv__navbar-heading_square'></div>
        <Link to='/'>
          <div className='cv__navbar-heading_links'>
            <h1 className='inline'>Alok Awasthi</h1>
            <p className='inline'> / SOFTWARE DEVELOPER </p>
          </div>
        </Link>
       </div>

      <ul className='cv__navbar-links'>
        <li>ABOUT ME</li>
        <li>RESUME</li>
        <li>EXPERIENCE</li>
        <li>CONTACT</li>
      </ul>

      <div className='cv__navbar-smallscreen'>
        <GiHamburgerMenu onClick={() => setToggleMenu(true)}/>

        {toggleMenu && (
          <div className='cv__navbar-smallscreen_overlay'>
            <MdOutlineRestaurantMenu fontSize={27} onClick={() => setToggleMenu(false)} />
            <ul className='cv__navbar-smallscreen_links'>
              <li>ABOUT ME</li>
              <li>RESUME</li>
              <li>EXPERIENCE</li>
              <li>CONTACT</li>
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}
export default Navbar;
